import React from 'react';
import { useAuth } from '../contexts/AuthContext';

const Cart = () => {
    const { cartItems, addToCart, removeFromCart } = useAuth();

    const handleAddToCart = (item) => {
        addToCart(item);
    };

    const handleRemoveFromCart = (itemId) => {
        removeFromCart(itemId);
    };

    return (
        <div>
            <h1>Cart Items</h1>
            <ul>
                {cartItems.map((item) => (
                    <li key={item.id}>
                        {item.name} - <button onClick={() => handleRemoveFromCart(item.id)}>Remove</button>
                    </li>
                ))}
            </ul>
            <button onClick={() => handleAddToCart({ id: 1, name: 'Item 1' })}>Add Item 1</button>
            <button onClick={() => handleAddToCart({ id: 2, name: 'Item 2' })}>Add Item 2</button>
        </div>
    );
};

export default Cart;
