import React, { useState, useEffect } from 'react';
import axios from 'axios';

const MenuItems = () => {
    const [categories, setCategories] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get('https://www.themealdb.com/api/json/v1/1/filter.php?c=Chicken');
                setCategories(response.data.meals);
            } catch (error) {
                console.error('Error fetching categories:', error);
            }
        };
        fetchData();
    }, []);

    return (
      <div className="bg-white">
          <div className="menu-wrapper">
              <h1>Food Categories</h1>
              <div className="categories-list">
                  {categories.map(meals => (
                      <div key={meals.idMeal} className="category-item">
                          <img src={meals.strMealThumb} alt={meals.strCategory} />
                          <h3>{meals.strMeal}</h3>
                      </div>
                  ))}
              </div>
          </div>

          <style jsx>{`
              .categories-list {
                  display: grid;
                  grid-template-columns: repeat(auto-fill, minmax(200px, 1fr));
                  grid-gap: 20px;
              }

              .category-item {
                  display: flex;
                  flex-direction: column;
                  align-items: center;
                  border: 1px solid #ccc;
                  border-radius: 8px;
                  padding: 20px;
                  transition: transform 0.3s ease-in-out;
              }

              .category-item:hover {
                  transform: translateY(-5px);
              }

              .category-item img {
                  max-width: 100%;
                  height: auto;
                  border-radius: 8px;
                  margin-bottom: 10px;
              }

              .category-item h3 {
                  font-size: 18px;
                  font-weight: 500;
                  text-align: center;
              }
          `}</style>
      </div>
  );
}

export default Meni;
